import React from 'react';
import s from'./Dialogs.module.css';
import DialogItem from './DialogItem/DialogItem';
import Message from './DialogItem/Message/Message';

const Dialogs = (props) => {

    // DATA

    let dialogs = [
        {id: 1, name: 'Name1'},
        {id: 2, name: 'Name2'},
        {id: 3, name: 'Name3'},
        {id: 4, name: 'Name4'}
    ];

    let messages = [
        {id: 1, message: 'Heyo'},
        {id: 2, message: 'Hi'},
        {id: 3, message: 'Hello'},
        {id: 4, message: 'Salam'},
        {id: 5, message: 'Ola'}
    ];

    // DATA END


    // MAPPPING TO THE COMPONENTS WITH PROPS

    let dialogsElements = dialogs
        .map( (dialogItem) => (<DialogItem name={dialogItem.name} id={dialogItem.id}/>));



    let messagesItems = messages
        .map( (messagesItem) => (<Message message={messagesItem.message} id={messagesItem.id}/>));

    //  MAPPING END



    return (
        <div className={s.dialogs}>
            <div>Dialogs:
                { dialogsElements }
            </div>
            <div>Messages from the dialog:
                { messagesItems }
            </div>
        </div>
    );
}

export default Dialogs;
