window.state = state;

let store = {
    state :  {

        profilePage: {
            posts: [
                {id:1, content: 'Post1', likes: 10},
                {id:1, content: 'Post2', likes: 20},
                {id:1, content: 'Post3', likes: 30},
                {id:1, content: 'Post4', likes: 40}
            ],
            mainPhoto: 'https://www.w3schools.com/howto/img_avatar.png',
            newPostText: 'dich'
        },

        dialogPage: {
            dialogs: [
                {id: 1, name: 'Name1'},
                {id: 2, name: 'Name2'},
                {id: 3, name: 'Name3'},
                {id: 4, name: 'Name4'}
            ],
            messages: [
                {id: 1, message: 'Heyo'},
                {id: 2, message: 'Hi'},
                {id: 3, message: 'Hello'},
                {id: 4, message: 'Salam'},
                {id: 5, message: 'Ola'}
            ]
        }

    },
    rerenderEntireTree() {
        console.log('CHANGED');
    },
    addPost() {
        let newPost = {
            id: 5,
            content: state.profilePage.newPostText,
            likes: 0
        };
        state.profilePage.posts.push(newPost);
        state.profilePage.newPostText = '';
        this.rerenderEntireTree(state);

    },
    updateNewPost(text) {
        state.profilePage.newPostText = text;
        this.rerenderEntireTree(state);
    },
    subscribe(observer) {
        this.rerenderEntireTree = observer;
    }
}

export default store;
