const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';

const initialState = {
    users: [
        {id: 1, followed: true, fullname: 'name1 sur1', location: {city: 'city1', country: 'country1'}},
        {id: 2, followed: false, fullname: 'name2 sur2', location: {city: 'city2', country: 'country2'}},
        {id: 3, followed: false, fullname: 'name3 sur3', location: {city: 'city3', country: 'country3'}}
    ]
};

let usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW: {
            let stateCopy = { ...state};
            stateCopy.users = [...state.users];

        }

        case UNFOLLOW: {
            let stateCopy = { ...state};
        }

        default:
            return state;
    }
}

export default usersReducer;
